    ronan: '{"_location":"Black Dragon Castle","_start":{"flags":{},"responses":["","You see a guard with a large scar on one cheek."],"triggers":[{},{}],"editor_idx":1},"look":{"flags":{},"responses":["","You see a guard with a large scar on one cheek."],"triggers":[{},{}],"editor_idx":2},"name":{"flags":{},"responses":["","\\"Name\'s Ronan.\\""],"triggers":[{},{"set_flag":"knows_ronan"}],"editor_idx":3},"job":{"flags":{},"responses":["","\\"Until the war begins, I\'m keeping watch here at the keep.\\""],"triggers":[{},{}],"editor_idx":4},"scar":{"flags":{},"responses":["","He grimaces. \\"It\'s dangerous out there. Let\'s leave it at that.\\""],"triggers":[{},{}],"editor_idx":5},"_confused":{"flags":{},"responses":["","He grunts in confusion."],"triggers":[{},{}],"editor_idx":6},"bye":{"flags":{},"responses":["","\\"\'Bye.\\""],"triggers":[{},{"end_convo":1}],"editor_idx":7}}',
    lian: '{"_location":"Black Dragon Castle","_start":{"flags":{},"responses":["","You approach a guard with a small pouch in his hand. He tosses it in the air and catches it."],"triggers":[{},{}],"editor_idx":1},"look":{"flags":{},"responses":["","You see a guard with a small pouch in his hand."],"triggers":[{},{}],"editor_idx":2},"name":{"flags":{"flags_met":"self_on_duty"},"responses":["He tosses the pouch from hand to hand. \\"I\'m Lian.\\"","He shakes his head and tosses the pouch from hand to hand. \\"We do not reveal... oh heck, it\'s not like it\'s not obvious who I am, helmet or no. Hi, I\'m Lian.\\""],"triggers":[{"set_flag":"knows_lian"},{"set_flag":"knows_lian"}],"editor_idx":3},"job":{"flags":{},"responses":["","\\"I am one of the many castle guards.\\""],"triggers":[{},{}],"editor_idx":4},"_confused":{"flags":{},"responses":["","He looks puzzled. \\"Sorry?\\""],"triggers":[{},{}],"editor_idx":5},"pouch":{"flags":{},"responses":["","\\"Oh, this?\\" He shakes the pouch in his hand. \\"Just small pebbles. I use it to keep my reflexes sharp.\\" "],"triggers":[{},{}],"editor_idx":6},"bye":{"flags":{},"responses":["","\\"Stay safe. It\'s dangerous out there.\\""],"triggers":[{},{"end_convo":1}],"editor_idx":7}}',
